def trans(x):
    x = str(x)
    x = x.replace('【', '[')
    x = x.replace('】', ']')
    x = x.replace('（', '(')
    x = x.replace('）', ')')
    x = x.replace('〔', '(')
    x = x.replace('〕', ')')
    x = x.replace('）', ')')
    x = x.replace(' ', '')
    x = x.replace('—', '-')
    x = x.replace('--', '-')
    x = x.replace('_', '-')
    return x


def exbianma1(quyu,page,conp=[]):
    soup = BeautifulSoup(page, 'lxml')
    tmp = soup.find('style')
    if tmp is not None:
        tmp.clear()
    tmp = soup.find('script')
    if tmp is not None:
        tmp.clear()

    txt = re.sub('[^\u4E00-\u9Fa5a-zA-Z0-9:：\\/\-\_\,，、;\\\\.\—\[\]【】\〕\〔\（\）\(\)\“]', '', soup.text.strip())
    txt = trans(txt)

    if conp:
        sql = '''select patterns from %s.quyu_patterns where quyu='%s' ''' % (conp[-1], quyu)
        df = db_query(sql, dbtype='postgresql', conp=conp)
        patterns = df.iloc[0, 0]
        
    else:
        patterns_path = os.path.join(os.path.dirname(__file__), 'patterns.json')
        with open(patterns_path, encoding='utf-8') as f:
            patterns_json = json.load(f)
        patterns = patterns_json[quyu]
    patterns = eval(patterns)   # 暂时用这个。
    if not isinstance(patterns,list):
        raise ValueError('Patterns should be List.Please check it')
    # patterns = ["(皖[a-zA-Z][0-9][\-]{0,1}20[0-9]{2}[\-]{0,1}[a-zA-Z]{0,2}[\-]{0,1}[0-9]{3,4}[(]{0,1}[a-zA-Z]{0,2}[)]{0,1}[.、]{0,1})",
    #             "(皖[^\u4E00-\u9Fa5]{4,}号)", "(皖[a-zA-Z][0-9][a-zA-Z]{0,2}[\-]{0,1}[0-9]{4,7}[\-]{0,1}[0-9]{0,3}号{0,1}[.、]{0,1})",
    #             "(皖建施招[0-9A-Za-z\-\_]{5,})", "(青政采代{0,1}小{0,1}[0-9A-Za-z\-\_\(\)]{5,})", "M11[0-9\-]{4,}"]
    for p in patterns:
        text = re.findall(p, txt)
        if text != []:
            if text[0][-1] == "、" or text[0][-1] == ".":
                text = text[0][:-2]
                return text
            else:
                return text[0]

    return None
